import React, {Component} from 'react';

import {connect} from 'react-redux';
import {signIn, signOut} from '../actions/index';

/**
 * Google auth component which renders login/logout google buttons and uses gapi methods for creating a new auth object, signing in a user or signing out
 * please see docs for reference
 */

class GoogleAuth extends Component {
  //initialize user signed in to null [is not signed in] and set state to be loggerd in with the gapi methods to do so [see did mount]
  state = {
    isSignedIn: null,
  };

  //load the OATH system from google -> use did mount lifecycle method for requests that should happen immediately on load
  componentDidMount () {
    window.gapi.load ('client:auth2', () => {
      //this is async: returns a promise -> this is where we will initiate the nww auth instance to sign the user in
      window.gapi.client
        .init ({
          clientId: '106342742873-ntgcr1aoiros0i86ddb7uk5cvn5t1fi2.apps.googleusercontent.com',
          scope: 'email', //the list of scopes [methods of authorization]
        })
        .then (() => {
          //create a new auth reference to use for the user auth
          this.auth = window.gapi.auth2.getAuthInstance ();
          this.setState ({
            isSignedIn: this.auth.isSignedIn.get (), //in the docs to check user is signed in
          });
          this.auth.isSignedIn.listen (this.onAuthChange); //listen for the users current signin status (inn the gapi docs also) ALL THESE METHODS AND WEIRD CODE SNIPPETS ARE APART OF THE GAPI!!
        });
    });
    /*load up the oath library from googles API -> see video 217-218 in react course [docs: https://developers.google.com/identity/sign-in/web/reference#authentication]
    see these docs to see how to initiate an authentication system for the app
    */
  }

  //remember arrow functions always get bound to 'this' which is why event handlers and listeners always need to be bound, or invoked as a callback using arrow functions: for design adn simplicity, we will use this convention to make life easy
  //get the users current auth state
  onAuthChange = () => {
    this.setState ({
      isSignedIn: this.auth.isSignedIn.get (),
    });
  };

  onSignInClick = () => {
    this.auth.signIn ();
  };

  onSignOutClick = () => {
    this.auth.signOut ();
  };

  //helper method to do conditional rendering of button for sign in/sign out pertaining to the gapi auth
  renderAuthButton = () => {
    if (this.state.isSignedIn === null) {
      return null;
    } else if (this.state.isSignedIn) {
      return (
        <button className="ui red google button" onClick={this.onSignOutClick}>
          {/**did not use explicit constructor bind! used arrows and passed it as a callback here so its bound by default when using arrows.
          THIS IS THE BEST PARADIGM! DO NOT USE () ON THE END OR ELSE ITS IMMEDIATELY CALLED! WE WANT THIS TO BE A CALLBACK*/}
          <i className="google icon" />
          Sign out
        </button>
      );
    } else {
      return (
        <button className="ui red google button" onClick={this.onSignInClick}>
          <i className="google icon" />
          Sing in with Google
        </button>
      );
    }
  };

  render () {
    return this.renderAuthButton ();
  }
}

//use connect to hook in to store passing it all the action creators to dispatch to store: reducers will invoke logic, mapStateToProps() always does just that: maps state
export default connect (null, {signIn, signOut}) (GoogleAuth);
